// ...existing code...
import React, { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import CartContext from "./CartContext";

function Product() {
  const [isLoaded, setIsLoaded] = useState(false);
  const { addToCart, count } = useContext(CartContext);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  // products with category field
  const products = [
    { id: 1, name: "Aesthetic Vase", price: 499, image: "/images/vase.jpg", category: "Decor" },
    { id: 2, name: "Minimalist Lamp", price: 899, image: "/images/lamp.jpg", category: "Lighting" },
    { id: 3, name: "Wooden Shelf", price: 1299, image: "/images/table.jfif", category: "Furniture" },
    { id: 4, name: "Ceramic Mug Set", price: 299, image: "/images/mug.webp", category: "Kitchen" },
    { id: 5, name: "Wall Clock", price: 399, image: "/images/clock.webp", category: "Decor" },
    { id: 6, name: "Throw Pillow", price: 199, image: "/images/pillow.webp", category: "Textiles" },
    { id: 7, name: "Table Runner", price: 349, image: "/images/table.webp", category: "Textiles" },
    { id: 8, name: "Indoor Plant", price: 599, image: "/images/plant.webp", category: "Greenery" },
    { id: 9, name: "Scented Candle", price: 249, image: "/images/candle.webp", category: "Aromas" },
  ];

  const categories = ["All", ...Array.from(new Set(products.map((p) => p.category)))];

  const [activeCategory, setActiveCategory] = useState("All");
  const [query, setQuery] = useState("");

  const handleAddToCart = (product) => {
    addToCart(product);
  };

  const filtered = products.filter((p) => {
    const matchesCategory = activeCategory === "All" || p.category === activeCategory;
    const matchesQuery = query.trim() === "" || p.name.toLowerCase().includes(query.toLowerCase());
    return matchesCategory && matchesQuery;
  });

  const styles = {
    container: {
      display: "flex",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      background: "linear-gradient(135deg, #fbeee6 0%, #f6d6c3 100%)",
      minHeight: "100vh",
      overflow: "hidden",
    },
    sidebar: {
      width: "220px",
      background: "rgba(255, 255, 255, 0.85)",
      backdropFilter: "blur(10px)",
      padding: "24px",
      boxShadow: "4px 0 12px rgba(0,0,0,0.08)",
      display: "flex",
      flexDirection: "column",
      justifyContent: "space-between",
      borderTopRightRadius: "18px",
      borderBottomRightRadius: "18px",
      transform: isLoaded ? "translateX(0)" : "translateX(-100%)",
      transition: "transform 0.8s ease",
    },
    logoBox: { textAlign: "center", marginBottom: "48px" },
    logoImg: { width: "80px", height: "80px", borderRadius: "50%", objectFit: "cover", marginBottom: "12px" },
    logo: {
      fontSize: "28px",
      fontWeight: "900",
      background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
      WebkitBackgroundClip: "text",
      WebkitTextFillColor: "transparent",
      letterSpacing: "2px",
      marginBottom: "8px",
    },
    main: { flex: 1, padding: "48px" },
    controls: { display: "flex", gap: 12, alignItems: "center", marginTop: 8, marginBottom: 18, flexWrap: "wrap" },
    categories: { display: "flex", gap: 8, flexWrap: "wrap" },
    catBtn: (active) => ({
      padding: "8px 12px",
      borderRadius: 12,
      border: active ? "none" : "1px solid rgba(0,0,0,0.06)",
      background: active ? "linear-gradient(90deg,#6366f1,#8b5cf6)" : "#fff",
      color: active ? "#fff" : "#111",
      cursor: "pointer",
      fontWeight: 700,
      boxShadow: active ? "0 8px 20px rgba(99,102,241,0.12)" : "0 2px 6px rgba(0,0,0,0.03)",
    }),
    search: { marginLeft: "auto", display: "flex", gap: 8, alignItems: "center" },
    input: { padding: "8px 12px", borderRadius: 10, border: "1px solid #e6e6e6", minWidth: 180 },
    productGrid: {
      display: "grid",
      gridTemplateColumns: "repeat(auto-fill, minmax(220px, 1fr))",
      gap: "32px",
      marginTop: "12px",
    },
    productCard: {
      background: "#fff",
      padding: "20px",
      borderRadius: "16px",
      boxShadow: "0 4px 12px rgba(0,0,0,0.08)",
      textAlign: "center",
      height: "320px",
      display: "flex",
      flexDirection: "column",
      justifyContent: "space-between",
      transition: "transform 180ms ease, box-shadow 180ms ease",
    },
    productImage: { width: "100%", height: "160px", objectFit: "cover", borderRadius: "12px", marginBottom: "12px" },
    smallMeta: { color: "#6b7280", fontSize: 13, marginTop: 6 },
    addBtn: { background: "#6366f1", color: "#fff", border: "none", padding: "10px 16px", borderRadius: "8px", cursor: "pointer", fontWeight: "600" },
    emptyNotice: { padding: 24, textAlign: "center", background: "#fff", borderRadius: 12 },
  };

  return (
    <div style={styles.container}>
      <aside style={styles.sidebar}>
        <div>
          <div style={styles.logoBox}>
            <img src="/images/chan2.jpg" alt="Logo" style={styles.logoImg} />
            <div style={styles.logo}>Serene Home</div>
            <p style={{ fontSize: 14, color: "#6366f1" }}>Where modern meets premium ‚ú®</p>
          </div>

          <ul style={{ listStyle: "none", padding: 0 }}>
            <li style={{ marginBottom: 8 }}>
              <Link to="/" style={{ textDecoration: "none", color: "inherit" }}>üè† Home</Link>
            </li>
            <li style={{ marginBottom: 8 }}>
              <Link to="/product" style={{ textDecoration: "none", color: "inherit" }}>üõçÔ∏è Product</Link>
            </li>
            <li style={{ marginBottom: 8 }}>
              <Link to="/cart" style={{ textDecoration: "none", color: "inherit" }}>üõí Cart</Link>
              {count > 0 && <span style={{ marginLeft: 8, background: "#dc2626", color: "#fff", padding: "4px 8px", borderRadius: 12, fontWeight: 700 }}>{count}</span>}
            </li>
          </ul>
        </div>

        <div style={{ marginTop: "auto" }}>
          <div style={{ marginBottom: 8, color: "#6b7280" }}>Need Help?</div>
          <button style={{ background: "#6366f1", color: "#fff", padding: "10px 16px", borderRadius: 8, border: "none", cursor: "pointer" }}>Customer Service</button>
        </div>
      </aside>

      <main style={styles.main}>
        <h1>üõçÔ∏è Product Page</h1>
        <p>Welcome ‚Äî filter by category or search to find items.</p>

        <div style={styles.controls}>
          <div style={styles.categories}>
            {categories.map((cat) => (
              <button
                key={cat}
                onClick={() => { setActiveCategory(cat); }}
                style={styles.catBtn(activeCategory === cat)}
              >
                {cat}
              </button>
            ))}
          </div>

          <div style={styles.search}>
            <input
              aria-label="search"
              placeholder="Search products..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              style={styles.input}
            />
            <button onClick={() => { setQuery(""); setActiveCategory("All"); }} style={{ padding: "8px 10px", borderRadius: 8, border: "1px solid #e6e6e6", background: "#fff", cursor: "pointer" }}>
              Reset
            </button>
          </div>
        </div>

        {filtered.length === 0 ? (
          <div style={styles.emptyNotice}>
            <div style={{ fontWeight: 800, marginBottom: 6 }}>No products found</div>
            <div style={{ color: "#6b7280" }}>Try another category or search term.</div>
          </div>
        ) : (
          <div style={styles.productGrid}>
            {filtered.map((product) => (
              <div key={product.id} style={styles.productCard} onMouseEnter={(e) => (e.currentTarget.style.transform = "translateY(-6px)") } onMouseLeave={(e) => (e.currentTarget.style.transform = "translateY(0)") }>
                <img src={product.image} alt={product.name} style={styles.productImage} onError={(e) => (e.currentTarget.src = "/images/placeholder.png")} />
                <div>
                  <h3 style={{ margin: 6 }}>{product.name}</h3>
                  <div style={styles.smallMeta}>{product.category} ¬∑ <strong>‚Ç±{product.price}</strong></div>
                </div>
                <div style={{ display: "flex", gap: 8, justifyContent: "center", marginTop: 8 }}>
                  <button onClick={() => handleAddToCart(product)} style={styles.addBtn}>Add to Cart</button>
                  <Link to={`/product/${product.id}`} style={{ textDecoration: "none" }}>
                    <button style={{ padding: "10px 14px", borderRadius: 8, border: "1px solid #e6e6e6", background: "#fff", cursor: "pointer" }}>Details</button>
                  </Link>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

export default Product;
