import React from "react";
import Sidebar from "./Sidebar";

function Cart() {
  const cartItems = [
    { id: 1, name: "Sample Product 1", price: 299 },
    { id: 2, name: "Sample Product 2", price: 499 },
  ];

  const total = cartItems.reduce((sum, item) => sum + item.price, 0);

  return (
    <div style={{ display: "flex" }}>
      {/* Sidebar */}
      <Sidebar cartCount={cartItems.length} />

      {/* Main Content */}
      <div style={{ flex: 1, padding: "20px" }}>
        <h1>ðŸ›’ Cart Page</h1>
        {cartItems.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <div>
            <ul>
              {cartItems.map((item) => (
                <li key={item.id}>
                  {item.name} - â‚±{item.price}
                </li>
              ))}
            </ul>
            <h3>Total: â‚±{total}</h3>
          </div>
        )}
      </div>
    </div>
  );
}

export default Cart;
