import React, { useState, useEffect } from "react";

function Landing() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [activeCard, setActiveCard] = useState(null);
  const [cartCount, setCartCount] = useState(0);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  const styles = {
    container: {
      display: "flex",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
      minHeight: "100vh",
      overflow: "hidden",
      position: "relative",
    },
    bgParticles: {
      position: "absolute",
      top: 0,
      left: 0,
      width: "100%",
      height: "100%",
      pointerEvents: "none",
      zIndex: 0,
    },
    particle: {
      position: "absolute",
      width: "4px",
      height: "4px",
      background: "rgba(255,255,255,0.3)",
      borderRadius: "50%",
      animation: "float 6s ease-in-out infinite",
    },
    sidebar: {
      width: "280px",
      background: "rgba(255,255,255,0.95)",
      backdropFilter: "blur(20px)",
      padding: "32px 24px",
      boxShadow: "0 8px 32px rgba(0,0,0,0.1)",
      display: "flex",
      flexDirection: "column",
      justifyContent: "space-between",
      borderTopRightRadius: "24px",
      borderBottomRightRadius: "24px",
      transform: isLoaded ? "translateX(0)" : "translateX(-100%)",
      transition: "transform 0.8s cubic-bezier(0.68, -0.55, 0.265, 1.55)",
      zIndex: 2,
      position: "relative",
    },
    logoBox: {
      textAlign: "center",
      marginBottom: "48px",
      transform: isLoaded ? "scale(1)" : "scale(0)",
      transition: "transform 0.6s ease-out 0.3s",
    },
    logo: {
      fontSize: "32px",
      fontWeight: "900",
      background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
      WebkitBackgroundClip: "text",
      WebkitTextFillColor: "transparent",
      letterSpacing: "3px",
      marginBottom: "8px",
      animation: "pulse 2s ease-in-out infinite",
    },
    tagline: {
      fontSize: "14px",
      color: "#6366f1",
      fontStyle: "italic",
      opacity: 0.8,
    },
    menu: {
      listStyle: "none",
      padding: 0,
      margin: 0,
    },
    menuItem: {
      display: "flex",
      alignItems: "center",
      gap: "16px",
      padding: "16px 20px",
      color: "#1f2937",
      fontWeight: "600",
      textDecoration: "none",
      cursor: "pointer",
      borderRadius: "16px",
      transition: "all 0.3s cubic-bezier(0.4, 0, 0.2, 1)",
      marginBottom: "8px",
      position: "relative",
      overflow: "hidden",
      transform: isLoaded ? "translateX(0)" : "translateX(-50px)",
      opacity: isLoaded ? 1 : 0,
    },
    helpBox: {
      marginTop: "32px",
      padding: "24px",
      background: "linear-gradient(135deg, rgba(99,102,241,0.1) 0%, rgba(139,92,246,0.1) 100%)",
      borderRadius: "20px",
      textAlign: "center",
      fontSize: "16px",
      color: "#6366f1",
      boxShadow: "0 8px 32px rgba(99,102,241,0.1)",
      transform: isLoaded ? "translateY(0)" : "translateY(50px)",
      transition: "transform 0.6s ease-out 0.8s",
      border: "1px solid rgba(99,102,241,0.2)",
    },
    main: {
      flex: 1,
      padding: "48px",
      display: "grid",
      gridTemplateColumns: "2fr 1fr",
      gap: "48px",
      position: "relative",
      zIndex: 1,
    },
    section: {
      background: "rgba(255,255,255,0.95)",
      backdropFilter: "blur(20px)",
      padding: "36px",
      borderRadius: "24px",
      boxShadow: "0 20px 60px rgba(0,0,0,0.1)",
      marginBottom: "32px",
      minWidth: "0",
      transform: isLoaded ? "translateY(0)" : "translateY(100px)",
      opacity: isLoaded ? 1 : 0,
      transition: "all 0.8s cubic-bezier(0.4, 0, 0.2, 1)",
      border: "1px solid rgba(255,255,255,0.2)",
      position: "relative",
      overflow: "hidden",
    },
    sectionTitle: {
      fontSize: "28px",
      fontWeight: "800",
      marginBottom: "24px",
      background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
      WebkitBackgroundClip: "text",
      WebkitTextFillColor: "transparent",
      letterSpacing: "1px",
      position: "relative",
    },
    productCard: {
      display: "flex",
      gap: "32px",
      alignItems: "center",
      flexWrap: "wrap",
      position: "relative",
    },
    productImg: {
      width: "240px",
      height: "180px",
      objectFit: "cover",
      borderRadius: "20px",
      boxShadow: "0 16px 40px rgba(0,0,0,0.15)",
      background: "linear-gradient(135deg, #f1f5f9 0%, #e2e8f0 100%)",
      border: "3px solid rgba(255,255,255,0.5)",
      transition: "all 0.4s cubic-bezier(0.4, 0, 0.2, 1)",
      cursor: "pointer",
    },
    productInfo: {
      flex: 1,
      minWidth: "200px",
    },
    productName: {
      fontSize: "24px",
      fontWeight: "700",
      color: "#1f2937",
      marginBottom: "8px",
      letterSpacing: "0.5px",
    },
    productDesc: {
      fontSize: "16px",
      color: "#6b7280",
      margin: "12px 0",
      lineHeight: "1.6",
    },
    productPrice: {
      color: "#059669",
      fontWeight: "800",
      fontSize: "24px",
      marginBottom: "20px",
      textShadow: "0 2px 4px rgba(5,150,105,0.3)",
    },
    buttons: {
      display: "flex",
      gap: "16px",
      justifyContent: "center",
      marginTop: "16px",
    },
    btn: {
      padding: "14px 28px",
      borderRadius: "50px",
      border: "none",
      cursor: "pointer",
      fontWeight: "600",
      fontSize: "16px",
      boxShadow: "0 8px 24px rgba(0,0,0,0.15)",
      transition: "all 0.3s cubic-bezier(0.4, 0, 0.2, 1)",
      position: "relative",
      overflow: "hidden",
      letterSpacing: "0.5px",
    },
    cartBtn: {
      background: "linear-gradient(135deg, #10b981 0%, #059669 100%)",
      color: "#fff",
    },
    buyBtn: {
      background: "linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)",
      color: "#fff",
    },
    rightCard: {
      background: "rgba(255,255,255,0.95)",
      backdropFilter: "blur(20px)",
      borderRadius: "20px",
      padding: "24px",
      boxShadow: "0 16px 40px rgba(0,0,0,0.1)",
      marginBottom: "32px",
      textAlign: "center",
      transform: isLoaded ? "translateX(0)" : "translateX(100px)",
      opacity: isLoaded ? 1 : 0,
      transition: "all 0.8s cubic-bezier(0.4, 0, 0.2, 1)",
      border: "1px solid rgba(255,255,255,0.3)",
      cursor: "pointer",
      position: "relative",
      overflow: "hidden",
    },
    rightImg: {
      width: "100%",
      height: "140px",
      objectFit: "cover",
      borderRadius: "16px",
      marginBottom: "16px",
      background: "linear-gradient(135deg, #f1f5f9 0%, #e2e8f0 100%)",
      border: "2px solid rgba(255,255,255,0.5)",
      transition: "all 0.4s cubic-bezier(0.4, 0, 0.2, 1)",
    },
    cartBadge: {
      position: "absolute",
      top: "-8px",
      right: "-8px",
      background: "linear-gradient(135deg, #ef4444 0%, #dc2626 100%)",
      color: "white",
      borderRadius: "50%",
      width: "24px",
      height: "24px",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      fontSize: "12px",
      fontWeight: "bold",
      animation: cartCount > 0 ? "bounce 0.6s ease-out" : "none",
    },
    shimmer: {
      position: "absolute",
      top: "-50%",
      left: "-50%",
      width: "200%",
      height: "200%",
      background: "linear-gradient(45deg, transparent 40%, rgba(255,255,255,0.3) 50%, transparent 60%)",
      transform: "rotate(45deg)",
      animation: "shimmer 2s infinite",
    },
  };

  const keyframes = `
    @keyframes float {
      0%, 100% { transform: translateY(0px) rotate(0deg); }
      33% { transform: translateY(-20px) rotate(5deg); }
      66% { transform: translateY(-10px) rotate(-5deg); }
    }
    @keyframes pulse {
      0%, 100% { transform: scale(1); }
      50% { transform: scale(1.05); }
    }
    @keyframes shimmer {
      0% { transform: translateX(-100%) translateY(-100%) rotate(45deg); }
      100% { transform: translateX(100%) translateY(100%) rotate(45deg); }
    }
    @keyframes bounce {
      0%, 20%, 53%, 80%, 100% { transform: translateY(0px); }
      40%, 43% { transform: translateY(-8px); }
      70% { transform: translateY(-4px); }
      90% { transform: translateY(-2px); }
    }
  `;

  const handleAddToCart = () => {
    setCartCount(prev => prev + 1);
  };

  const handleMouseEnter = (e, type) => {
    if (type === 'button') {
      e.target.style.transform = "translateY(-2px) scale(1.05)";
      e.target.style.boxShadow = "0 12px 32px rgba(0,0,0,0.25)";
    } else if (type === 'card') {
      e.currentTarget.style.transform = "translateY(-8px)";
      e.currentTarget.style.boxShadow = "0 24px 60px rgba(0,0,0,0.2)";
    } else if (type === 'image') {
      e.target.style.transform = "scale(1.05) rotate(1deg)";
      e.target.style.boxShadow = "0 20px 48px rgba(0,0,0,0.25)";
    } else if (type === 'menu') {
      e.currentTarget.style.background = "linear-gradient(135deg, rgba(99,102,241,0.1) 0%, rgba(139,92,246,0.1) 100%)";
      e.currentTarget.style.transform = "translateX(8px)";
      e.currentTarget.style.boxShadow = "0 8px 24px rgba(99,102,241,0.2)";
    }
  };

  const handleMouseLeave = (e, type) => {
    if (type === 'button') {
      e.target.style.transform = "translateY(0) scale(1)";
      e.target.style.boxShadow = "0 8px 24px rgba(0,0,0,0.15)";
    } else if (type === 'card') {
      e.currentTarget.style.transform = "translateY(0)";
      e.currentTarget.style.boxShadow = "0 16px 40px rgba(0,0,0,0.1)";
    } else if (type === 'image') {
      e.target.style.transform = "scale(1) rotate(0deg)";
      e.target.style.boxShadow = "0 16px 40px rgba(0,0,0,0.15)";
    } else if (type === 'menu') {
      e.currentTarget.style.background = "transparent";
      e.currentTarget.style.transform = "translateX(0)";
      e.currentTarget.style.boxShadow = "none";
    }
  };

  const particles = Array.from({ length: 20 }, (_, i) => (
    <div
      key={i}
      style={{
        ...styles.particle,
        left: `${Math.random() * 100}%`,
        top: `${Math.random() * 100}%`,
        animationDelay: `${Math.random() * 6}s`,
        opacity: Math.random() * 0.5 + 0.1,
      }}
    />
  ));

  return (
    <div style={styles.container}>
      <style>{keyframes}</style>
      <div style={styles.bgParticles}>{particles}</div>
      <aside style={styles.sidebar}>
        <div>
          <div style={styles.logoBox}>
            <div style={styles.logo}>Serene Home</div>
            <p style={styles.tagline}>Where modern meet premium ‚ú®</p>
          </div>
          <ul style={styles.menu}>
            {["üè† Home", "üõçÔ∏è Product", "üõí Cart", "üìñ About us", "üìû Contact"].map((item, index) => (
              <li
                key={item}
                style={{
                  ...styles.menuItem,
                  transitionDelay: `${0.1 + index * 0.1}s`,
                  position: "relative",
                }}
                onMouseEnter={(e) => handleMouseEnter(e, 'menu')}
                onMouseLeave={(e) => handleMouseLeave(e, 'menu')}
              >
                {item}
                {item.includes('Cart') && cartCount > 0 && (
                  <div style={styles.cartBadge}>{cartCount}</div>
                )}
              </li>
            ))}
          </ul>
        </div>
        <div style={styles.helpBox}>
          <p style={{ marginBottom: "16px", fontSize: "18px", fontWeight: "600" }}>Need Help? ü§ù</p>
          <button
            style={{ ...styles.btn, ...styles.buyBtn }}
            onMouseEnter={(e) => handleMouseEnter(e, 'button')}
            onMouseLeave={(e) => handleMouseLeave(e, 'button')}
          >
            Customer Service
          </button>
        </div>
      </aside>
      <main style={styles.main}>
        <div>
          {/* Latest Release */}
          <div
            style={{
              ...styles.section,
              transitionDelay: "0.2s",
            }}
            onMouseEnter={(e) => handleMouseEnter(e, 'card')}
            onMouseLeave={(e) => handleMouseLeave(e, 'card')}
          >
            <div style={styles.shimmer}></div>
            <h3 style={styles.sectionTitle}>üöÄ Latest Release</h3>
            <div style={styles.productCard}>
              <div style={{ position: "relative" }}>
                <img
                  src="/images/tv1.avif"
                  alt="Smart TV"
                  style={styles.productImg}
                  onMouseEnter={(e) => handleMouseEnter(e, 'image')}
                  onMouseLeave={(e) => handleMouseLeave(e, 'image')}
                />
              </div>
              <div style={styles.productInfo}>
                <p style={styles.productName}>Samsung 75" Smart TV</p>
                <p style={styles.productDesc}>
                  Experience cinema-quality entertainment with cutting-edge display technology and smart features.
                </p>
                <p style={styles.productPrice}>‚Ç±50,000</p>
                <div style={styles.buttons}>
                  <button
                    style={{ ...styles.btn, ...styles.cartBtn }}
                    onMouseEnter={(e) => handleMouseEnter(e, 'button')}
                    onMouseLeave={(e) => handleMouseLeave(e, 'button')}
                    onClick={handleAddToCart}
                  >
                    Add to Cart üõí
                  </button>
                  <button
                    style={{ ...styles.btn, ...styles.buyBtn }}
                    onMouseEnter={(e) => handleMouseEnter(e, 'button')}
                    onMouseLeave={(e) => handleMouseLeave(e, 'button')}
                  >
                    Buy Now ‚ö°
                  </button>
                </div>
              </div>
            </div>
          </div>
          {/* Must Have Item */}
          <div
            style={{
              ...styles.section,
              transitionDelay: "0.4s",
            }}
            onMouseEnter={(e) => handleMouseEnter(e, 'card')}
            onMouseLeave={(e) => handleMouseLeave(e, 'card')}
          >
            <div style={styles.shimmer}></div>
            <h3 style={styles.sectionTitle}>‚≠ê Must Have Item</h3>
            <div style={styles.productCard}>
              <div style={{ position: "relative" }}>
                <img
                  src="/images/table.jpg"
                  alt="Wooden Table"
                  style={styles.productImg}
                  onMouseEnter={(e) => handleMouseEnter(e, 'image')}
                  onMouseLeave={(e) => handleMouseLeave(e, 'image')}
                />
              </div>
              <div style={styles.productInfo}>
                <p style={styles.productName}>Minimalist Wooden Table</p>
                <p style={styles.productDesc}>
                  Modern design with premium oak wood, perfect for contemporary homes and offices.
                </p>
                <p style={styles.productPrice}>‚Ç±1,300</p>
                <div style={styles.buttons}>
                  <button
                    style={{ ...styles.btn, ...styles.cartBtn }}
                    onMouseEnter={(e) => handleMouseEnter(e, 'button')}
                    onMouseLeave={(e) => handleMouseLeave(e, 'button')}
                    onClick={handleAddToCart}
                  >
                    Add to Cart üõí
                  </button>
                  <button
                    style={{ ...styles.btn, ...styles.buyBtn }}
                    onMouseEnter={(e) => handleMouseEnter(e, 'button')}
                    onMouseLeave={(e) => handleMouseLeave(e, 'button')}
                  >
                    Buy Now ‚ö°
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        {/* Right Column */}
        <div>
          <div
            style={{
              ...styles.rightCard,
              transitionDelay: "0.6s",
            }}
            onMouseEnter={(e) => handleMouseEnter(e, 'card')}
            onMouseLeave={(e) => handleMouseLeave(e, 'card')}
          >
            <div style={styles.shimmer}></div>
            <img
              src="/images/sofa.jpg"
              alt="L-Shape Sofa"
              style={styles.rightImg}
              onMouseEnter={(e) => handleMouseEnter(e, 'image')}
              onMouseLeave={(e) => handleMouseLeave(e, 'image')}
            />
            <p style={styles.productName}>Comfy L Shape Sofa</p>
            <p style={styles.productPrice}>‚Ç±17,000</p>
          </div>
          <div
            style={{
              ...styles.rightCard,
              transitionDelay: "0.8s",
            }}
            onMouseEnter={(e) => handleMouseEnter(e, 'card')}
            onMouseLeave={(e) => handleMouseLeave(e, 'card')}
          >
            <div style={styles.shimmer}></div>
            <img
              src="/images/chandelier.jpg"
              alt="Chandelier"
              style={styles.rightImg}
              onMouseEnter={(e) => handleMouseEnter(e, 'image')}
              onMouseLeave={(e) => handleMouseLeave(e, 'image')}
            />
            <p style={styles.productName}>Premium Chandelier</p>
            <p style={styles.productPrice}>‚Ç±120,000</p>
          </div>
        </div>
      </main>
    </div>
  );
}

export default Landing;
