import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Product() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [cartCount, setCartCount] = useState(0);

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  const products = [
    {
      id: 1,
      name: "Aesthetic Vase",
      price: 499,
      image: "/images/vase.jpg",
    },
    {
      id: 2,
      name: "Minimalist Lamp",
      price: 899,
      image: "/images/lamp.jpg",
    },
    {
      id: 3,
      name: "Wooden Shelf",
      price: 1299,
      image: "/images/table.jpg",
    },
  ];

  const handleAddToCart = () => {
    setCartCount(cartCount + 1);
  };

  const styles = {
    container: {
      display: "flex",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      background: "linear-gradient(135deg, #fbeee6 0%, #f6d6c3 100%)",
      minHeight: "100vh",
      overflow: "hidden",
      position: "relative",
    },
    sidebar: {
      width: "220px",
      background: "rgba(255, 255, 255, 0.85)",
      backdropFilter: "blur(10px)",
      padding: "24px",
      boxShadow: "4px 0 12px rgba(0,0,0,0.08)",
      display: "flex",
      flexDirection: "column",
      justifyContent: "space-between",
      borderTopRightRadius: "18px",
      borderBottomRightRadius: "18px",
      transform: isLoaded ? "translateX(0)" : "translateX(-100%)",
      transition: "transform 0.8s cubic-bezier(0.68, -0.55, 0.265, 1.55)",
      zIndex: 2,
      position: "relative",
    },
    logoBox: {
      textAlign: "center",
      marginBottom: "48px",
      transform: isLoaded ? "scale(1)" : "scale(0)",
      transition: "transform 0.6s ease-out 0.3s",
    },
    logoImg: {
      width: "80px",
      height: "80px",
      borderRadius: "50%",
      objectFit: "cover",
      marginBottom: "12px",
    },
    logo: {
      fontSize: "28px",
      fontWeight: "900",
      background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
      WebkitBackgroundClip: "text",
      WebkitTextFillColor: "transparent",
      letterSpacing: "2px",
      marginBottom: "8px",
      animation: "pulse 2s ease-in-out infinite",
    },
    tagline: {
      fontSize: "14px",
      color: "#6366f1",
      fontStyle: "italic",
      opacity: 0.8,
    },
    menu: { listStyle: "none", padding: 0, margin: 0 },
    menuItem: {
      display: "flex",
      alignItems: "center",
      gap: "16px",
      padding: "16px 20px",
      color: "#1f2937",
      fontWeight: "600",
      textDecoration: "none",
      cursor: "pointer",
      borderRadius: "16px",
      transition: "all 0.3s ease",
      marginBottom: "8px",
      position: "relative",
      overflow: "hidden",
      transform: isLoaded ? "translateX(0)" : "translateX(-50px)",
      opacity: isLoaded ? 1 : 0,
    },
    cartBadge: {
      position: "absolute",
      top: "-8px",
      right: "-8px",
      background: "linear-gradient(135deg, #ef4444 0%, #dc2626 100%)",
      color: "white",
      borderRadius: "50%",
      width: "24px",
      height: "24px",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      fontSize: "12px",
      fontWeight: "bold",
      animation: cartCount > 0 ? "bounce 0.6s ease-out" : "none",
    },
    helpBox: {
      marginTop: "32px",
      padding: "24px",
      background:
        "linear-gradient(135deg, rgba(99,102,241,0.1) 0%, rgba(139,92,246,0.1) 100%)",
      borderRadius: "20px",
      textAlign: "center",
      fontSize: "16px",
      color: "#6366f1",
      boxShadow: "0 8px 32px rgba(99,102,241,0.1)",
      border: "1px solid rgba(99,102,241,0.2)",
    },
    main: {
      flex: 1,
      padding: "48px",
      display: "flex",
      flexDirection: "column",
      alignItems: "flex-start",
      justifyContent: "center",
    },
  };

  const keyframes = `
    @keyframes pulse {
      0%, 100% { transform: scale(1); }
      50% { transform: scale(1.05); }
    }
    @keyframes bounce {
      0%, 100% { transform: translateY(0); }
      50% { transform: translateY(-6px); }
    }
  `;

  return (
    <div style={styles.container}>
      <style>{keyframes}</style>
      <aside style={styles.sidebar}>
        <div>
          <div style={styles.logoBox}>
            {/* <images/chan2.jpg */}
            <div style={styles.logo}>Serene Home</div>
            <p style={styles.tagline}>Where modern meets premium ‚ú®</p>
          </div>
          <ul style={styles.menu}>
            <li style={styles.menuItem}>
              <Link to="/" style={{ textDecoration: "none", color: "inherit" }}>
                üè† Home
              </Link>
            </li>
            <li style={styles.menuItem}>
              <Link to="/product" style={{ textDecoration: "none", color: "inherit" }}>
                üõçÔ∏è Product
              </Link>
            </li>
            <li style={styles.menuItem}>
              <Link to="/cart" style={{ textDecoration: "none", color: "inherit" }}>
                üõí Cart
              </Link>
              {cartCount > 0 && <div style={styles.cartBadge}>{cartCount}</div>}
            </li>
            <li style={styles.menuItem}>
              <Link to="/about" style={{ textDecoration: "none", color: "inherit" }}>
                üìñ About Us
              </Link>
            </li>
            <li style={styles.menuItem}>
              <Link to="/contact" style={{ textDecoration: "none", color: "inherit" }}>
                üìû Contact
              </Link>
            </li>
          </ul>
        </div>
        <div style={styles.helpBox}>
          <p style={{ marginBottom: "16px", fontWeight: "600" }}>Need Help? ü§ù</p>
          <p style={{ marginBottom: "16px", fontWeight: "600" }}>
            About Account Management or ordering & Payment refund and FAQ
          </p>
          <button style={{ background: "#6366f1", color: "#fff", padding: "10px 16px", borderRadius: "8px", border: "none", cursor: "pointer" }}>
            Customer Service
          </button>
        </div>
      </aside>

      <main style={styles.main}>
        <h1>üõçÔ∏è Product Page</h1>
        <p>
          Welcome to the <strong>Product Page</strong>! Choose your favorite items below.
        </p>

        <div style={{ display: "flex", gap: "32px", marginTop: "32px", flexWrap: "wrap" }}>
          {products.map((product) => (
            <div
              key={product.id}
              style={{
                background: "#fff",
                padding: "20px",
                borderRadius: "16px",
                boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
                width: "220px",
                textAlign: "center",
              }}
            >
              <img
                src={product.image}
                alt={product.name}
                style={{ width: "100%", borderRadius: "12px", marginBottom: "12px" }}
              />
              <h3 style={{ marginBottom: "8px" }}>{product.name}</h3>
              <p style={{ marginBottom: "12px", fontWeight: "bold" }}>‚Ç±{product.price}</p>
              <button
                onClick={handleAddToCart}
                style={{
                  background: "#6366f1",
                  color: "#fff",
                  border: "none",
                  padding: "10px 16px",
                  borderRadius: "8px",
                  cursor: "pointer",
                  fontWeight: "600",
                }}
              >
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

export default Product;
